import React, {useState} from 'react'
import {Form,Button,} from 'react-bootstrap'

 const SearchBox = ({history}) => {
    const [keyword,setKeyword]= useState('')

    const submitHandler= (e) =>{
        e.preventDefault()
        if(keyword.trim()){
             history.push(`/search/${keyword}`)
        } else {
            history.push('/products')
        }
    }

    return (
        <Form onSubmit={submitHandler} inline>
            <span>
            <Form.Control type='text' name='q' onChange={(e) =>setKeyword(e.target.value)} placeholder='Search Products...' className='py-0.715'></Form.Control>
            <Button type='submit' variant='outline-success' className='py-0.715'>Search</Button>
            </span>
        </Form>
    )
}

export default SearchBox
