import { SELLER_DETAILS_FAIL, SELLER_DETAILS_REQUEST, SELLER_DETAILS_SUCCESS, SELLER_LIST_FAIL, SELLER_LIST_REQUEST, SELLER_LIST_SUCCESS } from '../constants/sellerConstants'
import axios from 'axios'

export const listSellers= () => async(dispatch) =>{
    try{
        dispatch({type:SELLER_LIST_REQUEST})
        const {data}= await axios.get('/api/sellers')
        dispatch({type:SELLER_LIST_SUCCESS,
        payload:data,
        })
} catch(error){
    dispatch({type:SELLER_LIST_FAIL,
     payload: error.response && error.response.data.message?error.response.data.message:error.message,
    })
}
}

